struct TaskDetailView: View {
    @Environment(\.dismiss) private var dismiss
    @ObservedObject var viewModel: TaskViewModel
    @State private var showingEditSheet = false
    @State private var showingDeleteAlert = false
    let task: TaskTask

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 20) {
                // Header
                HStack(spacing: 16) {
                    Circle()
                        .fill(priorityColor)
                        .frame(width: 12, height: 12)

                    Text(task.title)
                        .font(.title2)
                        .fontWeight(.bold)
                        .strikethrough(task.isCompleted)
                        .foregroundColor(task.isCompleted ? .gray : .primary)

                    Spacer()
                }

                // Category
                Label(task.category.rawValue, systemImage: task.category.icon)
                    .foregroundColor(.secondary)

                // Due Date
                VStack(alignment: .leading, spacing: 4) {
                    Text("Due Date")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Text(task.dueDate.formatted(date: .long, time: .shortened))
                        .font(.body)
                        .foregroundColor(isOverdue ? .red : .primary)
                }

                // Description
                if !task.description.isEmpty {
                    VStack(alignment: .leading, spacing: 4) {
                        Text("Description")
                            .font(.subheadline)
                            .foregroundColor(.secondary)

                        Text(task.description)
                            .font(.body)
                    }
                }

                // Action Buttons
                VStack(spacing: 16) {
                    Button {
                        withAnimation {
                            viewModel.toggleTaskCompletion(task)
                        }
                    } label: {
                        Label(task.isCompleted ? "Mark as Incomplete" : "Mark as Complete",
                              systemImage: task.isCompleted ? "xmark.circle.fill" : "checkmark.circle.fill")
                            .font(.headline)
                            .foregroundColor(task.isCompleted ? .red : .green)
                            .frame(maxWidth: .infinity)
                            .padding()
                            .background(
                                RoundedRectangle(cornerRadius: 12)
                                    .stroke(task.isCompleted ? .red : .green, lineWidth: 2)
                            )
                    }

                    HStack(spacing: 16) {
                        // Edit Button
                        Button {
                            showingEditSheet = true
                        } label: {
                            Label("Edit", systemImage: "pencil")
                                .font(.headline)
                                .frame(maxWidth: .infinity)
