import Foundation
import SwiftUI

@MainActor
class TaskViewModel: ObservableObject {
    @Published var tasks: [Task] = []
    @Published var showingNewTaskSheet = false
    @Published var selectedFilter: TaskFilter = .all

    private let userDefaults = UserDefaults.standard
    private let tasksKey = "savedTasks"

    enum TaskFilter {
        case all, today, upcoming, completed

        var title: String {
            switch self {
            case .all: return "All Tasks"
            case .today: return "Today"
            case .upcoming: return "Upcoming"
            case .completed: return "Completed"
            }
        }
    }

    init() {
        loadTasks()
    }

    func addTask(_ task: Task) {
        tasks.append(task)
        saveTasks()
    }

    func deleteTask(_ task: Task) {
        tasks.removeAll { $0.id == task.id }
        saveTasks()
    }

    func toggleTaskCompletion(_ task: Task) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index].isCompleted.toggle()
            saveTasks()
        }
    }

    func updateTask(_ task: Task) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index] = task
            saveTasks()
        }
    }

    func filteredTasks(_ filter: TaskFilter = .all) -> [Task] {
        switch filter {
        case .all:
            return tasks.sorted { $0.dueDate < $1.dueDate }
        case .today:
            return tasks.filter { Calendar.current.isDateInToday($0.dueDate) }
                .sorted { $0.dueDate < $1.dueDate }
        case .upcoming:
            return tasks.filter { $0.dueDate > Date() && !$0.isCompleted }
                .sorted { $0.dueDate < $1.dueDate }
        case .completed:
            return tasks.filter { $0.isCompleted }
                .sorted { $0.dueDate < $1.dueDate }
        }
    }

    private func saveTasks() {
        if let encoded = try? JSONEncoder().encode(tasks) {
            userDefaults.set(encoded, forKey: tasksKey)
        }
    }

    private func loadTasks() {
        if let data = userDefaults.data(forKey: tasksKey),
           let decoded = try? JSONDecoder().decode([Task].self, from: data) {
            tasks = decoded
        }
    }
}

import SwiftUI

struct TaskRow: View {
    let task: Task
    let onToggle: () -> Void

    private var isOverdue: Bool {
        !task.isCompleted && task.dueDate < Date()
    }

    var body: some View {
        HStack(spacing: 16) {
            // Checkbox
            Button(action: onToggle) {
                ZStack {
                    Circle()
                        .strokeBorder(task.isCompleted ? Color.green : Color.gray, lineWidth: 2)
                        .frame(width: 24, height: 24)

                    if task.isCompleted {
                        Image(systemName: "checkmark")
                            .font(.system(size: 12, weight: .bold))
                            .foregroundColor(.green)
                    }
                }
            }

            VStack(alignment: .leading, spacing: 4) {
                HStack {
                    Text(task.title)
                        .font(.system(size: 16, weight: .semibold))
                        .strikethrough(task.isCompleted)
                        .foregroundColor(task.isCompleted ? .gray : .primary)

                    if task.reminderEnabled {
                        Image(systemName: "bell.fill")
                            .font(.system(size: 12))
                            .foregroundColor(.blue)
                    }
                }

                HStack(spacing: 8) {
                    Label(task.category.rawValue, systemImage: task.category.icon)
                        .font(.caption)
                        .foregroundColor(.gray)

                    Text(task.dueDate.formatted(date: .abbreviated, time: .shortened))
                        .font(.caption)
                        .foregroundColor(isOverdue ? .red : .gray)
                }
            }

            Spacer()

            // Priority indicator
            Circle()
                .fill(priorityColor)
                .frame(width: 12, height: 12)
        }
        .padding(.vertical, 8)
        .padding(.horizontal, 16)
        .background(
            RoundedRectangle(cornerRadius: 12)
                .fill(Color(.systemBackground))
                .shadow(color: Color.black.opacity(0.05), radius: 8, x: 0, y: 2)
        )
        .contentShape(Rectangle())
    }

    private var priorityColor: Color {
        switch task.priority {
        case .low:
            return .green
        case .medium:
            return .yellow
        case .high:
            return .red
        }
    }
}

import SwiftUI

struct NewTaskView: View {
    @Environment(\.dismiss) private var dismiss
    @ObservedObject var viewModel: TaskViewModel

    @State private var title = ""
    @State private var taskDescription = ""
    @State private var dueDate = Date()
    @State private var priority: Task.Priority = .medium
    @State private var category: Task.Category = .personal
    @State private var reminderEnabled = false
    @FocusState private var focusedField: Field?

    private enum Field {
        case title, description
    }

    var body: some View {
        NavigationView {
            Form {
                Section {
                    TextField("Task Title", text: $title)
                        .focused($focusedField, equals: .title)
                        .submitLabel(.next)

                    TextField("Description", text: $taskDescription, axis: .vertical)
                        .focused($focusedField, equals: .description)
                        .submitLabel(.done)
                        .lineLimit(3...6)
                }

                Section {
                    DatePicker("Due Date", selection: $dueDate, displayedComponents: [.date, .hourAndMinute])

                    Picker("Priority", selection: $priority) {
                        ForEach(Task.Priority.allCases, id: \.self) { priority in
                            Label(priority.rawValue, systemImage: "flag.fill")
                                .foregroundColor(priorityColor(for: priority))
                                .tag(priority)
                        }
                    }

                    Picker("Category", selection: $category) {
                        ForEach(Task.Category.allCases, id: \.self) { category in
                            Label(category.rawValue, systemImage: category.icon)
                                .tag(category)
                        }
                    }
                }

                Section {
                    Toggle("Enable Reminder", isOn: $reminderEnabled)
                }
            }
            .navigationTitle("New Task")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Cancel") {
                        dismiss()
                    }
                }

                ToolbarItem(placement: .confirmationAction) {
                    Button("Add") {
                        addTask()
                    }
                    .disabled(title.isEmpty)
                }

                ToolbarItem(placement: .keyboard) {
                    HStack {
                        Spacer()
                        Button("Done") {
                            focusedField = nil
                        }
                    }
                }
            }
            .onSubmit {
                switch focusedField {
                case .title:
                    focusedField = .description
                case .description:
                    focusedField = nil
                case .none:
                    break
                }
            }
        }
    }

    private func addTask() {
        let task = Task(
            title: title,
            description: taskDescription,
            dueDate: dueDate,
            priority: priority,
            category: category,
            reminderEnabled: reminderEnabled
        )
        viewModel.addTask(task)
        dismiss()
    }

    private func priorityColor(for priority: Task.Priority) -> Color {
        switch priority {
        case .low:
            return .green
        case .medium:
            return .yellow
        case .high:
            return .red
        }
    }
}

import SwiftUI

struct TaskListView: View {
    @StateObject private var viewModel = TaskViewModel()
    @State private var selectedTask: Task?

    var body: some View {
        NavigationStack {
            ZStack {
                if viewModel.tasks.isEmpty {
                    emptyStateView
                } else {
                    taskList
                }
            }
            .navigationTitle("TaskMaster")
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button {
                        viewModel.showingNewTaskSheet = true
                    } label: {
                        Image(systemName: "plus.circle.fill")
                            .font(.title3)
                    }
                }
            }
            .sheet(isPresented: $viewModel.showingNewTaskSheet) {
                NewTaskView(viewModel: viewModel)
            }
        }
    }

    private var taskList: some View {
        ScrollView {
            VStack(spacing: 12) {
                filterSegment

                LazyVStack(spacing: 12) {
                    ForEach(viewModel.filteredTasks(viewModel.selectedFilter)) { task in
                        TaskRow(task: task) {
                            withAnimation {
                                viewModel.toggleTaskCompletion(task)
                            }
                        }
                        .contextMenu {
                            Button(role: .destructive) {
                                withAnimation {
                                    viewModel.deleteTask(task)
                                }
                            } label: {
                                Label("Delete", systemImage: "trash")
                            }
                        }
                    }
                }
                .padding(.horizontal)
            }
        }
        .background(Color(.systemGroupedBackground))
    }

    private var filterSegment: some View {
        ScrollView(.horizontal, showsIndicators: false) {
            HStack(spacing: 12) {
                ForEach([TaskViewModel.TaskFilter.all,
                        .today,
                        .upcoming,
                        .completed], id: \.self) { filter in
                    filterButton(for: filter)
                }
            }
            .padding(.horizontal)
        }
    }

    private func filterButton(for filter: TaskViewModel.TaskFilter) -> some View {
        Button {
            withAnimation {
                viewModel.selectedFilter = filter
            }
        } label: {
            Text(filter.title)
                .font(.subheadline)
                .fontWeight(.medium)
                .padding(.horizontal, 16)
                .padding(.vertical, 8)
                .background(
                    Capsule()
                        .fill(viewModel.selectedFilter == filter ?
                              Color.blue : Color(.systemGray6))
                )
                .foregroundColor(viewModel.selectedFilter == filter ?
                               .white : .primary)
        }
    }

    private var emptyStateView: some View {
        VStack(spacing: 20) {
            Image(systemName: "checkmark.circle")
                .font(.system(size: 64))
                .foregroundColor(.blue)

            Text("No Tasks Yet")
                .font(.title2)
                .fontWeight(.bold)

            Text("Add your first task by tapping the + button")
                .font(.body)
                .foregroundColor(.secondary)
                .multilineTextAlignment(.center)
                .padding(.horizontal)

            Button {
                viewModel.showingNewTaskSheet = true
            } label: {
                Text("Add Task")
                    .font(.headline)
                    .foregroundColor(.white)
                    .padding(.horizontal, 24)
                    .padding(.vertical, 12)
                    .background(
                        Capsule()
                            .fill(Color.blue)
                    )
            }
        }
    }
}

